function () {
  var result = [];
  var itemId, categoryId;
  itemId = iProductNo;
  categoryId = iCategoryNo;

  var trTags = document.querySelectorAll('#totalProducts > table > tbody > tr'); 
  var optChk = document.querySelectorAll('ul.xans-product-option > li');
  var popupOpt = document.querySelectorAll('tbody.xans-product-option > tr > td');

  for (var i = 0; i < trTags.length; i++) {
      var element,title,optionTitle,price,qty;
      switch(trTags[i].className.trim()){
          case '':
              if (optChk.length) {
                continue;
              } else {
                element = trTags[i].querySelector('td');
                title = element.outerText.trim();
                element = trTags[i].querySelector('td:nth-child(3) > span > input');
                if ( element ) price = element.value;
                else continue;
              }
              break;

          case 'add_product':
              element = trTags[i].querySelector('td:nth-child(1) > p');
              element = element.outerText.split('- ');
              title = element[0].trim();

              optionTitle = '';
              element = trTags[i].querySelector('td:nth-child(1) > p > span');
              if (element) optionTitle = element.outerText.trim();

              element = trTags[i].querySelector("[class*='option_box'][class*='price']");
              price = element.value;
              break;

          case 'option_product':
              element = trTags[i].querySelector('td:nth-child(1) > p');
              element = element.outerText.split('- ');
              title = element[0].trim();

              optionTitle = '';
              element = trTags[i].querySelector('td:nth-child(1) > p > span');
              if (element) optionTitle = element.outerText.trim();

              element = trTags[i].querySelector("[class*='option_box'][class*='price']")
              price = element.value;
              break;
          default : 
              continue;
              break;              
      }

      element = trTags[i].querySelector("[id*='quantity']");
      if (element === null) break;
      qty = element.value;

      if (qty > 1) {
          price = price / qty;
      }

      title = title.trim().replace(/\s{2}/g, ' ');    
      result.push({
        'item_name': title,
        'item_id': itemId,
        'price': price,
        'item_category': categoryId,
        'item_variant':  optionTitle,
        'quantity': qty,
      });
  }

  return result;
}

