function() {
    var url = {{Page URL}};  
 	var params = {};
  	if (url.length){
  		var paramStr = url.split('?');
  		if (paramStr.length > 1) {
  			paramStr[1].split('&').forEach(function(item) {params[item.split("=")[0]] = item.split("=")[1]});
    	}
  	}

  	return params;
}
